// Vérifier si le payload est une chaîne JSON
try {
    // Tenter de parser la chaîne JSON dans le payload
    var jsonData = JSON.parse(msg.payload);

    // Maintenant vous pouvez accéder aux propriétés de jsonData
    msg.payload = {
        status: jsonData.status,
        expiration: jsonData.expiration
    };

} catch (e) {
    // Si le parsing échoue, afficher une erreur et conserver l'ancienne valeur
    node.error("Erreur de parsing JSON : " + e.message);

    // En cas d'erreur de parsing, récupérer la date d'expiration depuis les paramètres globaux
    msg.payload = {
        expiration: global.get('parametre.li.expiration'),
        erreur: "Problème serveur, mais licence toujours valide",
        status: true // Le statut reste à true, indiquant que la licence existe toujours
    };
}

// Fonction pour vérifier la date d'expiration avec le serveur
if (msg.payload.status === "true" || msg.payload.status === true) {
    // Licence existe, vérifier la date d'expiration
    const expirationDate = new Date(msg.payload.expiration); // Convertir la date d'expiration au format Date
    const currentDate = new Date();

    // Comparer les deux dates
    if (expirationDate < currentDate) {
        msg.payload.status = false;  // Licence expirée
        global.set('parametre.li.active', false); // Mettre à jour le statut dans les paramètres globaux
        global.set('parametre.li.expiration', expirationDate.toISOString()); // Enregistrer la date d'expiration
        msg.erreur = "License expirer"
        node.send(msg);
    } else {
        msg.payload.status = true; // Licence valide
        global.set('parametre.li.active', true); // Mettre à jour le statut dans les paramètres globaux
        global.set('parametre.li.expiration', expirationDate.toISOString()); // Enregistrer la date d'expiration
    }
} else {
    // Licence n'existe pas ou problème de serveur
    msg.payload.status = false; // Licence n'existe pas
    global.set('parametre.li.active', false); // Mettre à jour le statut dans les paramètres globaux
    global.set('parametre.li.expiration', null); // Pas de date d'expiration car la licence n'existe pas
    msg.erreur = "Licence n'existe pas"
    node.send(msg);
}

