// Vérifie que le payload est un Buffer
if (Buffer.isBuffer(msg.payload)) {
    // Convertit chaque octet en hexadécimal sur 2 chiffres
    msg.payload = msg.payload
        .toString('hex')                // Convertit tout le buffer en une chaîne hexadécimale continue
        .match(/.{1,2}/g)               // Coupe la chaîne tous les 2 caractères
        .join(' ') + '\n';              // Ajoute un espace entre chaque octet et un retour à la ligne
} else if (Array.isArray(msg.payload)) {
    // Si c'est un tableau, on le traite aussi
    msg.payload = msg.payload
        .map(x => x.toString(16).padStart(2, '0'))
        .join(' ') + '\n';
} else {
    // Sinon, on ne fait rien ou on peut lever une erreur
    msg.payload = 'Payload non reconnu\n';
}
