let currentAddress = global.get("slaveAddress-Trame3") || 1; // Valeur par défaut : 1
// Récupération de l'adresse esclave depuis le contexte global
const slaveId = global.get("slaveAddress-Trame3") || 0x01; // Valeur par défaut si non définie
const functionCode = 0x10;
const registerHigh = 0x16;
const registerLow = 0x20;
const quantityHigh = 0x00;
const quantityLow = 0x01;
const byteCount = 0x02;
const valueHigh = 0x00;
const valueLow = 0x00;

// Calculer CRC16 Modbus
function crc16(buffer) {
    let crc = 0xFFFF;
    for (let i = 0; i < buffer.length; i++) {
        crc ^= buffer[i];
        for (let j = 0; j < 8; j++) {
            if (crc & 0x0001) {
                crc = (crc >> 1) ^ 0xA001;
            } else {
                crc = crc >> 1;
            }
        }
    }
    return crc;
}

// Construction du buffer sans CRC
const dataBuffer = Buffer.from([
    slaveId,
    functionCode,
    registerHigh,
    registerLow,
    quantityHigh,
    quantityLow,
    byteCount,
    valueHigh,
    valueLow
]);

// Calcul du CRC
const crc = crc16(dataBuffer);

// Buffer final avec CRC
const finalBuffer = Buffer.allocUnsafe(dataBuffer.length + 2);
dataBuffer.copy(finalBuffer);
finalBuffer.writeUInt16LE(crc, dataBuffer.length);

msg.topic = "Trame-3-dynamiques"
msg.payload = finalBuffer;
return msg;

