var payload = msg.payload;
var baseTopic = "BMS_" + slaveIdNumber;
var discoveryPrefix = "homeassistant/sensor";
var deviceName = "BMS_" + slaveIdNumber;
var deviceID = "BMS_" + slaveIdNumber + "_device";
var dynamicDataTopic = `${baseTopic}/donnees_dynamiques`;

var topics = {};
var discoveryMsgs = [];
var dynamicData = {};

// Extraire uniquement les tensions des cellules
var cellVoltages = [];
var cellKeys = [];
for (var key in payload) {
    // Ne prendre que les clés qui contiennent "Cell" et "volt" et finissent par "_V"
    if (key.includes("Cell_") && key.includes("volt") && key.endsWith("_V")) {
        let voltage = Number(payload[key]);
        if (!isNaN(voltage)) {
            cellVoltages.push(voltage);
            cellKeys.push(key);
        }
    }
}

// Calculs des statistiques des cellules
if (cellVoltages.length > 0) {
    // Calcul de la moyenne
    var cellAvgVolt = cellVoltages.reduce((sum, voltage) => sum + voltage, 0) / cellVoltages.length;

    // Trouve la tension max et min
    var cellMaxVolt = Math.max(...cellVoltages);
    var cellMinVolt = Math.min(...cellVoltages);

    // Calcul du delta
    var cellDeltaVolt = cellMaxVolt - cellMinVolt;

    // Trouve les index des cellules
    var maxVoltIndex = cellKeys[cellVoltages.indexOf(cellMaxVolt)].match(/Cell_(\d+)/)[1];
    var minVoltIndex = cellKeys[cellVoltages.indexOf(cellMinVolt)].match(/Cell_(\d+)/)[1];

    // Ajoute les valeurs calculées au payload
    payload["cell_voltage_average_V"] = parseFloat(cellAvgVolt.toFixed(3));
    payload["cell_voltage_delta_V"] = parseFloat(cellDeltaVolt.toFixed(3));
    payload["cell_voltage_max_number_N"] = parseInt(maxVoltIndex);
    payload["cell_voltage_min_number_N"] = parseInt(minVoltIndex);
}

// Ajouter toutes les valeurs dynamiquement
for (var key in payload) {
    var cleanKey = key.replace(/_[A-Z]+$/, ""); // Supprime l'underscore et l'unité
    var sensorTopic = `${baseTopic}/${cleanKey}`;
    topics[sensorTopic] = payload[key];
    dynamicData[cleanKey] = payload[key];

    var configTopic = `${discoveryPrefix}/${deviceID}_${cleanKey}/config`;
    var configPayload = {
        name: cleanKey,
        unique_id: `${deviceID}_${cleanKey}`,
        state_topic: sensorTopic,
        unit_of_measurement: "",
        entity_category: "diagnostic",  // Ajoute cette catégorie pour grouper dans "Live data"
        device: {
            identifiers: [deviceID],
            name: deviceName,
            manufacturer: "Smartphoton-JK-BMS",
            model: "JK_PB2A16S15P",
            sw_version: "1.0.0"
        }
    };

    // Ajouter des unités de mesure spécifiques
    if (key.endsWith("_V")) configPayload.unit_of_measurement = "V";
    if (key.endsWith("_Ah")) configPayload.unit_of_measurement = "Ah";
    if (key.endsWith("_A")) configPayload.unit_of_measurement = "A";
    if (key.endsWith("_W")) configPayload.unit_of_measurement = "W";
    if (key.endsWith("_T")) configPayload.unit_of_measurement = "°C";
    if (key.endsWith("_R")) configPayload.unit_of_measurement = "Ω";
    if (key.endsWith("_P")) {
        configPayload.unit_of_measurement = "%";
        if (key.startsWith("SOC")) {
            configPayload.device_class = "battery";
        }
    }
    if (key.endsWith("_S")) configPayload.unit_of_measurement = "s";

    discoveryMsgs.push({ topic: configTopic, payload: JSON.stringify(configPayload), retain: true });
}

// Publier les données dynamiques sous un seul topic
topics[dynamicDataTopic] = JSON.stringify(dynamicData);

var outputMsgs = discoveryMsgs;
for (var topic in topics) {
