var discoveryMsgs = [];

// Ajouter toutes les valeurs dynamiquement
for (var key in payload) {
    var cleanKey = key.replace(/_[A-Z]+$/, "").toLowerCase();
    var sensorTopic = `${baseTopic}/state/${cleanKey}`;  // Simplifié le state_topic

    var configTopic = `${discoveryPrefix}/sensor/${deviceID}/${cleanKey}/config`;
    var configPayload = {
        name: `BMS ${key.replace(/_[A-Z]+$/, "").replace(/_/g, " ")}`,
        unique_id: `${deviceID}_${cleanKey}`,
        state_topic: sensorTopic,
        enabled_by_default: true,
        device: {
            identifiers: [deviceID],
            name: deviceName,
            manufacturer: "Smartphoton-JK-BMS",
            model: payload.BMS_A || "JK_PB2A16S15P",
            sw_version: payload.SW_N || "1.0.0"
        }
    };

    // Ajout des unités selon le suffixe
    if (key.endsWith("_S")) {
        configPayload.unit_of_measurement = "s";
        configPayload.device_class = "duration";
        configPayload.state_class = "measurement";
    }
    else if (key.endsWith("_N") && key.includes("Time")) {
        configPayload.unit_of_measurement = "s";
        configPayload.device_class = "duration";
        configPayload.state_class = "measurement";
    }
    else if (key.endsWith("_N") && !key.includes("Serial")) {
        configPayload.state_class = "measurement";
    }

    // Gestion des icônes
    if (key.includes("Password")) {
        configPayload.icon = "mdi:form-textbox-password";
    } else if (key.includes("Serial")) {
        configPayload.icon = "mdi:barcode";
    } else if (key.includes("protocol")) {
        configPayload.icon = "mdi:protocol";
    } else if (key.includes("version") || key.includes("SW") || key.includes("FW")) {
        configPayload.icon = "mdi:numeric-10";
    } else if (key.includes("Uptime")) {
        configPayload.icon = "mdi:timer-outline";
        configPayload.device_class = "duration";
    } else if (key.includes("date")) {
        configPayload.icon = "mdi:calendar";
    }

    // Ajouter le message de configuration
    discoveryMsgs.push({
        topic: configTopic,
        payload: JSON.stringify(configPayload),
        retain: true
    });

    // Ajouter le message d'état
    discoveryMsgs.push({
        topic: sensorTopic,
        payload: payload[key],
        retain: true
    });
}

return [discoveryMsgs];

