var discoveryMsgs = [];

// Fonction de conversion des booléens
function formatBoolean(value) {
    if (typeof value === 'boolean') {
        return value ? 'True' : 'False';
    }
    if (typeof value === 'string') {
        const lowerVal = value.toLowerCase();
        return lowerVal === 'true' ? 'True' : lowerVal === 'false' ? 'False' : value;
    }
    return value;
}

function createConfigPayload(key, value, commandTopic) {
    var cleanKey = key.replace(/_[A-Z]+$/, "").toLowerCase();
    var entityType = "number";

    if (key.endsWith("_B")) {
        entityType = "switch";
    }

    var configTopic = `${discoveryPrefix}/${entityType}/${deviceID}/${cleanKey}/config`;

    var configPayload = {
        name: `BMS ${key.replace(/_[A-Z]+$/, "").replace(/_/g, " ")}`,
        unique_id: `${deviceID}_${cleanKey}`,
        state_topic: `${baseTopic}/control/${cleanKey}/state`,
        command_topic: `${baseTopic}/control/${cleanKey}/set`,
        entity_category: "config",
        device: {
            identifiers: [deviceID],
            name: deviceName,
            manufacturer: "Smartphoton-JK-BMS",
            model: payload.BMS_A || "JK_PB2A16S15P",
            sw_version: "1.0.0"
        }
    };

    if (entityType === "number") {
        configPayload.mode = "box";

        if (key.endsWith("_V")) {
            configPayload.unit_of_measurement = "V";
            configPayload.min = 0;
            configPayload.max = 5;
            configPayload.step = 0.01;
        }

        // ... (autres configurations number inchangées)

    } else if (entityType === "switch") {
        // Modification clé ici pour Home Assistant
        configPayload.payload_on = "True";
        configPayload.payload_off = "False";
        configPayload.state_on = "True";
        configPayload.state_off = "False";
    }

    return {
        topic: configTopic,
        payload: JSON.stringify(configPayload),
        retain: true
    };
}

for (var key in payload) {
    discoveryMsgs.push(createConfigPayload(key, payload[key]));

    var cleanKey = key.replace(/_[A-Z]+$/, "").toLowerCase();
    var formattedValue = formatBoolean(payload[key]);

    discoveryMsgs.push({
        topic: `${baseTopic}/control/${cleanKey}/state`,
        payload: formattedValue,
        retain: true
    });
}

return [discoveryMsgs];

let currentAddress = global.get("slaveAddress-Trame1") || 1; // Valeur par défaut : 1
let Number_of_BMS = global.get("nb_jkbms") || 1; // Valeur par défaut : 1

// Incrémenter l'adresse, revenir à 1 après 8
currentAddress = currentAddress >= Number_of_BMS ? 1 : currentAddress + 1;

global.set("slaveAddress-Trame1", currentAddress);

