// Entrée attendue: msg.payload contient la tension (ex: 3.42)
function generateModbusWriteCommand(voltage) {
    // Fonction de calcul CRC16 Modbus
    function crc16(buffer) {
        let crc = 0xFFFF;
        for (let i = 0; i < buffer.length; i++) {
            crc ^= buffer[i];
            for (let j = 0; j < 8; j++) {
                if (crc & 0x0001) {
                    crc = (crc >> 1) ^ 0xA001;
                } else {
                    crc = crc >> 1;
                }
            }
        }
        return crc;
    }

    // Conversion de la tension en valeur entière (échelle 1/1000)
    const voltageValue = Math.round(voltage * 1000);

    // Construction de la trame sans CRC
    // Récupération de l'adresse esclave depuis le contexte global
    const slaveId = global.get("slaveAddress-setup") || 0x01; // Valeur par défaut si non définie

    const command = [
        slaveId,                           // Slave ID
        0x10,                           // Function code (16/0x10 - Write Multiple Registers)
        0x10,                           // Register address high byte (0x10)
        0x84,                           // Register address low byte (0x84)
        0x00,                           // Quantity high byte (2 registres pour uint32)
        0x02,                           // Quantity low byte (2 registres pour uint32)
        0x04,                           // Byte count (4 bytes pour uint32)
        (voltageValue >> 24) & 0xFF,    // Valeur MSB (premier octet)
        (voltageValue >> 16) & 0xFF,    // Deuxième octet
        (voltageValue >> 8) & 0xFF,     // Troisième octet
        voltageValue & 0xFF             // Valeur LSB (dernier octet)
    ];

    // Calcul du CRC
    const crc = crc16(Buffer.from(command));

    // Ajout du CRC à la trame (en ordre inverse car little-endian)
    command.push(crc & 0xFF);          // CRC low byte
    command.push((crc >> 8) & 0xFF);   // CRC high byte

    return Buffer.from(command);
}

