if (Buffer.isBuffer(payload)) payload = Array.from(payload);

// Fonction utilitaire pour décoder le type de trame
function typeTrame(byte) {
    if (byte === 1) return "setup";
    if (byte === 2) return "data";
    return "autre";
}

// Trame "master" (broadcast) : commence par 55 aa eb 90
if (
    payload.length > 5 &&
    payload[0] === 0x55 &&
    payload[1] === 0xaa &&
    payload[2] === 0xeb &&
    payload[3] === 0x90
) {
    msg.bms_adresse = "master";
    msg.bms_type = typeTrame(payload[4]); // offset 4
    // On garde le payload tel quel
    return msg;
}

// Trame "courte" : premier byte = adresse (de 1 à 15)
if (
    payload.length > 16 &&
    payload[0] >= 0x01 &&
    payload[0] <= 0x0F
) {
    msg.bms_adresse = payload[0];
    msg.bms_type = typeTrame(payload[15]); // offset 15

    // On cherche le début de la vraie trame (0x55, 0xAA)
    let start = -1;
    for (let i = 0; i < payload.length - 1; i++) {
        if (payload[i] === 0x55 && payload[i+1] === 0xAA) {
            start = i;
            break;
        }
    }
    if (start !== -1) {
  let newPayload = payload.slice(start);
  // Conversion explicite en Buffer pour compatibilité parser
  msg.payload = Buffer.from(newPayload);
  return msg;
} else {
  // Si pas de 0x55 0xAA trouvé, on ignore ou on passe la trame brute
  return null;
}

}

// Pour les autres trames, ignorer ou traiter différemment
return null;

// === Décodage trames CAN JKBMS - Dernière mise à jour ===

function parseCanFrame(payload) {
    const regex = /can0\s+([A-F0-9]+)\s+\[8\]\s+([A-F0-9\s]+)/;
    const match = payload.match(regex);
    if (!match) return null;
    const canId = match[1];
    const dataHex = match[2].trim().split(/\s+/);
    const data = dataHex.map(hex => parseInt(hex, 16));
    return { canId, data };
}

function decode2F4(data) {
    const totalVoltage = ((data[1] << 8) | data[0]) * 0.1;
    const soc = data[4];
    let rawCurrent = (data[3] << 8) | data[2];
    if (rawCurrent > 32767) rawCurrent -= 65536;
    const current = (rawCurrent * 0.1) - 400;
    return {
        type: "main_status",
        totalVoltage,
        soc,
        current
    };
}

function decode07F4(data) {
    let alarmBits = 0;
    for (let i = 0; i < 4; i++) {
        alarmBits |= data[i] << (8 * i);
    }
    const alarmList = [
        "Cell overvoltage",
        "Cell undervoltage",
        "Total voltage overvoltage",
        "Total voltage undervoltage",
        "Large pressure difference of monomer",
        "Discharge overcurrent",
        "Charge overcurrent",
        "Temperature is too high",
        "Temperature is too low",
        "Excessive temperature difference",
        "SOC too low",
        "Insulation is too low",
        "High voltage interlock fault",
        "External communication failure",
        "Internal communication failure"
    ];
    const alarms = [];
    for (let i = 0; i < alarmList.length; i++) {
        const level = (alarmBits >> (i * 2)) & 0x03;
        if (level > 0) {
            alarms.push({
                code: i + 1,
                name: alarmList[i],
                level,
                levelText: ["", "Serious", "Important", "General"][level]
            });
        }
    }
    return {
        type: "alarm_info",
        alarms,
        alarmCount: alarms.length,
        rawAlarmBits: alarmBits
    };
}

function decode4F4(data) {
    const deltaCellVoltage = data[0] * 0.001;
    const maxCellVoltage = ((data[1] << 8) | data[0]) * 0.001;
    const maxCellPosition = data[2];
    const minCellVoltage = ((data[4] << 8) | data[3]) * 0.001;
    const minCellPosition = data[5];
    return {
        type: "cell_minmax",
        deltaCellVoltage,
        maxCellVoltage,
        maxCellPosition,
        minCellVoltage,
        minCellPosition
    };
}

function decode5F4(data) {
    const maxTemp = data[0] - 50;
    const maxTempPosition = data[1];
    const minTemp = data[2] - 50;
    const minTempPosition = data[3];
    const avgTemp = data[4] - 50;
    return {
        type: "temperatures",
        maxTemp,
        maxTempPosition,
        minTemp,
        minTempPosition,
        avgTemp
    };
}

function decode18F128F4(data) {
    let current = (data[1] << 8) | data[0];
    if (current > 32767) current -= 65536;
    current *= 0.001;
    const power = ((data[3] << 8) | data[2]) * 0.1;
    const cycleCount = (data[7] << 8) | data[6];
    return {
        type: "power_current",
        current,
        power,
        cycleCount
    };
}

// ✅ MISE À JOUR : ID 01F21400
function decode01F21400(data) {
    // Byte 0–1 : Voltage (unsigned), × 0.01
    const voltage = ((data[1] << 8) | data[0]) * 0.01;

    // Byte 2–3 : Current (signed), × 0.1
    let rawCurrent = (data[3] << 8) | data[2];
    if (rawCurrent > 32767) rawCurrent -= 65536;
    const current = rawCurrent * 0.1;

    // Byte 4–5 : Temperature (signed), ÷ 10
    let rawTemp = (data[5] << 8) | data[4];
    if (rawTemp > 32767) rawTemp -= 65536;
    const temperature = rawTemp / 10;

    // Byte 6–7 : Cycle count (unsigned)
    const cycleCount = (data[7] << 8) | data[6];

    return {
        type: "monitoring",
        voltage,       // en V
        current,       // en A
        temperature,   // en °C
        cycleCount
    };
}

function decode18F528F4(data) {
    const alarmCode = data[0];
    const cycleCount = (data[2] << 8) | data[1];
    const alarmMap = {
        0: "Normal",
        1: "Cell overvoltage",
        2: "Cell undervoltage",
        3: "Pack overvoltage",
        4: "Pack undervoltage",
        5: "Charge overcurrent",
        6: "Discharge overcurrent",
        7: "Charge overtemp",
        8: "Discharge overtemp",
        9: "Charge undertemp",
        10: "Discharge undertemp",
        11: "MOS overtemp",
        12: "Short circuit",
        13: "BMS overtemp",
        14: "BMS undertemp",
        23: "Cell imbalance",
        35: "System normal"
    };
    return {
        type: "cycle_count",
        alarmCode,
        alarmText: alarmMap[alarmCode] || `Unknown alarm ${alarmCode}`,
        cycleCount
    };
}

function decode1806E5F4(data) {
    const value = (data[3] << 8) | data[2];
    return {
        type: "status_data",
        value,
        byte0: data[0],
        byte1: data[1],
        rawBytes: data
    };
}

function decode18F228F4(data) {
    const temps = [];
    let mosTemp = null;
    for (let i = 1; i < 6; i++) {
        if (data[i] !== 0) {
            const temp = data[i] - 50;
            temps.push(temp);
        }
    }
    if (temps.length > 0) {
        mosTemp = temps[0];
    }
    return {
        type: "individual_temps",
        temperatures: temps,
        mosTemp,
        sensorCount: temps.length,
        byte0: data[0]
    };
}

function decode18F428F4(data) {
    const systemData = (data[1] << 8) | data[0];
    const socAlt = data[6];
    return {
        type: "alt_soc_data",
        systemData,
        socAlt,
        byte2: data[2],
        rawBytes: data
    };
}

function decodeCellVoltages(canId, data) {
    const baseId = parseInt(canId.substring(3, 4));
    const cellStart = baseId * 4;
    const voltages = [];
    for (let i = 0; i < 8; i += 2) {
        const voltage = ((data[i + 1] << 8) | data[i]) * 0.001;
        voltages.push({
            cellNumber: cellStart + (i / 2) + 1,
            voltage
        });
    }
    return {
        type: "cell_voltages",
        group: baseId,
        voltages
    };
}

function decodeExperimental(canId, data) {
    const patterns = [];
    for (let i = 0; i < data.length; i++) {
        if (data[i] >= 70 && data[i] <= 80) {
            patterns.push({
                type: "possible_mos_temp",
                position: i,
                value: data[i] - 50,
                rawValue: data[i]
            });
        }
    }
    for (let i = 0; i < data.length - 1; i++) {
        const value16 = (data[i + 1] << 8) | data[i];
        if (value16 >= 10 && value16 <= 50) {
            patterns.push({
                type: "possible_cycle_count",
                position: i,
                value: value16,
                rawBytes: [data[i], data[i + 1]]
            });
        }
    }
    return {
        type: "experimental",
        canId,
        patterns,
        rawData: data
    };
}

// === LOGIQUE PRINCIPALE ===
const parsed = parseCanFrame(msg.payload);
if (!parsed) {
    return {
        payload: {
            timestamp: new Date().toISOString(),
            canId: "",
            type: "parse_error",
            note: "Erreur parsing trame CAN"
        }
    };
}

const { canId, data } = parsed;
let decoded;

switch (canId) {
    case "2F4":
        decoded = decode2F4(data);
        break;
    case "4F4":
        decoded = decode4F4(data);
        break;
    case "5F4":
        decoded = decode5F4(data);
        break;
    case "7F4":
        decoded = decode07F4(data);
        break;
    case "18F128F4":
        decoded = decode18F128F4(data);
        break;
    case "01F21400":
        decoded = decode01F21400(data);
        break;
    case "18F528F4":
        decoded = decode18F528F4(data);
        break;
    case "1806E5F4":
        decoded = decode1806E5F4(data);
        break;
    case "18F228F4":
        decoded = decode18F228F4(data);
        break;
    case "18F428F4":
        decoded = decode18F428F4(data);
        break;
    default:
        if (canId.match(/^18E[0-3]28F4$/)) {
            decoded = decodeCellVoltages(canId, data);
        } else {
            decoded = decodeExperimental(canId, data);
        }
        break;
}

let bmsId = "unknown";
if (decoded.type === "cycle_count") {
    bmsId = decoded.cycleCount === 35 ? "BMS2" : "BMS1";
} else if (decoded.type === "power_current" && typeof decoded.cycleCount !== "undefined") {
    bmsId = decoded.cycleCount === 35 ? "BMS2" : (decoded.cycleCount === 12 ? "BMS1" : "unknown");
} else if (decoded.type === "individual_temps" && typeof decoded.mosTemp === "number") {
    bmsId = decoded.mosTemp > 24 ? "BMS2" : "BMS1";
}

return {
    payload: {
        timestamp: new Date().toISOString(),
        canId,
        bmsId,
        rawData: data,
        rawHex: data.map(b => b.toString(16).padStart(2, '0').toUpperCase()).join(' '),
        ...decoded
    }
};

