// Node-RED function node pour générer la trame Modbus
function generateModbusCommand() {
    // Fonction de calcul CRC16 Modbus
    function crc16(buffer) {
        let crc = 0xFFFF;
        for (let i = 0; i < buffer.length; i++) {
            crc ^= buffer[i];
            for (let j = 0; j < 8; j++) {
                if (crc & 0x0001) {
                    crc = (crc >> 1) ^ 0xA001;
                } else {
                    crc = crc >> 1;
                }
            }
        }
        return crc;
    }

    // Construction de la trame sans CRC
    const command = [
        0x01,       // Slave ID
        0x03,       // Function code (03 - Read Holding Registers)
        0x11,       // Register address high byte
        0x14,       // Register address low byte
        0x00,       // Quantity high byte (1 registre, 2 octets)
        0x01        // Quantity low byte
    ];

    // Calcul du CRC
    const crc = crc16(Buffer.from(command));

    // Ajout du CRC à la trame (en ordre inverse car little-endian)
    command.push(crc & 0xFF);         // CRC low byte
    command.push((crc >> 8) & 0xFF);  // CRC high byte

    return Buffer.from(command);
}

// Envoi de la trame
msg.topic = "LCD-on-off"
msg.payload = generateModbusCommand();
return msg;

