msg.extractBits = function(response) {
    if (response && response.length >= 5) {
        const uint16Value = (response[3] << 8) | response[4];
        let bitStates = {};
        for (let i = 0; i < 9; i++) {
            bitStates[`bit${i}`] = (uint16Value >> i) & 1;
        }
        return bitStates;
    }
    return null;
};

